import React from "react";
import { Component } from "react";

class DisplayCropOverview extends Component {


    constructor(props){
        super(props)
        this.color ={
          0:"green",
          1:"yellow",
          '':"orange",
          4:"red"

      }
      this.state=[
        "Harvested",  
    "Processed",  
    "Packed",     // 2
    "ForSale",    // 3
    "Sold",       // 4
    "Shipped",    // 5
    "Received",   // 6
    "Purchased"
      ]
        }

    render(){

        return(

            <div >

              { this.props.crops.map((crop, key) => {
                    let x=crop.farmstate.toNumber
                return(
                  <div className="product" key={key} >

                   <div className="product_organic"style={{background:this.color[crop.farmstate]}} > this.state[x]</div>
                    <ul id="postList" className="list-group list-group-flush">
                      <li className="list-group-item">
                      <p>UPC :   <input  value={crop.upc.toNumber()}/></p>  
                      <p>NAME: <input value={crop.cropName} /> </p>
                      <p>Price:  {crop.price.toNumber()} </p>
                      <p>KG:  {crop.kg.toNumber()} </p>
                        {/* <p>ABOUT:   {product.productdescription}</p> */}

                      </li>
                    </ul>
                    </div>

                )   
              }
              )
            }


            </div>


        )
    }
}

export default DisplayCropOverview;
