import React from "react";
import { Component } from "react";

class DisplayUnionOverview extends Component {

  constructor(props){
    super(props)
    this.cropcolor ={
      0:"green",
      1:"yellow",

  }
  this.cropstate=[

    "Harvested",  
"Processed",  
  ]
  this.salecolor={

    0:"orange",
    1:"blue",
    2:"red"
  }
  this.salestate=[

    "Crowdsourcing",
    "ForSale",
    "Sold",
  ]
  this.state={
    cropid:0
  }


    }


    render(){

        return(

            <div >

              { this.props.unions.map((union, key) => {

                return(

                  <div className="product" key={key} >
                    <div className="product_organic"style={{background:this.cropcolor[union.cropstate]}} > {this.cropstate[union.cropstate]}</div>
                    <div className="product_organic"style={{background:this.salecolor[union.productState]}} > {this.salestate[union.productState]}</div>
                    <ul id="postList" className="list-group list-group-flush">
                      <li className="list-group-item">
                      <p>UPC :   <input  value={union.upc.toNumber()}/></p>
                      <p>Union Number :   <input  value={union.uNumber.toNumber()}/></p>  
                      <p>Crop NAME: <input value={union.cropName} /> </p>
                      <p>Farmer perkg Price:  <input value={window.web3.utils.fromWei(union.priceperkg.toString(), 'Ether')} />Eth </p>
                      <p>Location:  <input value={union.location}/></p>
                      <p>No. People Joined:  <input value={union.presentmember}/></p>
                      <p>Max kg:  <input value={union.maxkg}/></p>
                      <p>Recievd kg:  <input value={union.receivedkg}/></p>
                      {union.productState==0 ? <p>Enter Crop ID to Join <input
                            id="cropid"
                            type="number"

                            placeholder="Enter the cropid"
                            required on 
                            onChange={(event)=>{
                              event.preventDefault()
                               this.state.cropid=event.target.value;
                            }} /> 


                      <button 
                              onClick={(event)=>{

                                event.preventDefault()

                                let unionid=union.uNumber.toNumber()
                                this.props.joinunion(event,this.state.cropid,unionid)
                              }}

                            > Join Union </button> </p> :
                            <p> Price :  <input value={window.web3.utils.fromWei(union.priceforsale.toString(), 'Ether')} />Eth 
                            <button onClick={(event)=>{
                               event.preventDefault()
                               this.props.unionCroppurchase(event,this.state.cropid,unionid)

                            }}>Buy </button></p>
                             }

                      </li>
                    </ul>
                    </div>

                )
              }
              )
            }


            </div>


        )
    }
}

export default DisplayUnionOverview;
