import React from "react";
import { Component } from "react";

class DisplayUnionOverview extends Component {

  constructor(props){
    super(props)
    this.cropcolor ={
      0:"green",
      1:"yellow",

  }
  this.cropstate=[

    "Harvested",  
"Processed",  
  ]
  this
  this.salestate=[

    "Crowdsourcing",
    "ForSale",
    "Sold",
  ]

    }


    render(){

        return(

            <div >

              { this.props.unions.map((union, key) => {
                return(
                  <div className="product" key={key} >
                    <div className="product_organic"style={{background:this.cropcolor[union.cropstate]}} > {this.cropstate[union.cropstate]}</div>
                    <div className="product_organic"style={{background:this.cropcolor[union.cropstate]}} > {this.cropstate[union.cropstate]}</div>
                    <ul id="postList" className="list-group list-group-flush">
                      <li className="list-group-item">
                      <p>UPC :   <input  value={union.upc.toNumber()}/></p>
                      <p>Union Number :   <input  value={union.uNumber.toNumber()}/></p>  
                      <p>Crop NAME: <input value={union.cropName} /> </p>
                      <p>Price:  {union.priceforsale.toNumber()} </p>
                        {/* <p>ABOUT:   {product.productdescription}</p> */}

                      </li>
                    </ul>
                    </div>

                )
              }
              )
            }


            </div>


        )
    }
}

export default DisplayUnionOverview;
