import React from "react";
import { Component } from "react";

class DisplayProductOverview extends Component {


  constructor(props){
    super(props)
    this.cropcolor ={
      0:"green",
      1:"yellow",

  }
  this.cropstate=[

    "Harvested",  
"Processed",  
  ]
  this.salecolor={

    0:"orange",
    1:"blue",
    2:"red"
  }
  this.salestate=[

    "Crowdsourcing",
    "ForSale",
    "Sold",
  ]
  this.state={
    cropid:0
  }


    }

    render(){

        return(

            <div >

              { this.props.products.map((product, key) => {
                return(
                  <div className="product" key={key} >
                     <div className="product_organic"style={{background:this.salecolor[product.salestate]}} > {this.salestate[product.salestate]}</div>

                    <ul id="postList" className="list-group list-group-flush">
                      <li className="list-group-item">
                      <p>UPC :   <input  value={product.upc.toNumber()}/></p>  
                      <p>NAME: <input value={product.productname} /> </p>
                      <p>Price:  <input value={window.web3.utils.fromWei(product.price.toString(), 'Ether')} />Eth </p>
                        {/* <p>ABOUT:   {product.productdescription}</p> */}
                   {/* <p>ABOUT:   {product.productdescription}</p> */}
                  ? <p> Owner:<input value={product.productowner}/></p>
                   {product.salestate==1 ?
                        <button 
                              onClick={(event)=>{

                                event.preventDefault()
                                let price=window.web3.utils.fromWei(product.price.toString(), 'Ether')
                                this.props.productPurchase(event,product.productNumber.toNumber(),price)
                              }}

                            > Buy Prduct </button> :
                             <p>New Owner:<input value={product.Distributor}/></p>}

                      </li>
                    </ul>
                    </div>

                )
              }
              )
            }


            </div>


        )
    }
}

export default DisplayProductOverview;
