import React from "react";
import { Component } from "react";

class DisplayUnionOverview extends Component {

  constructor(props){
    super(props)
    this.color ={
      0:"green",
      1:"yellow",
      '':"orange",
      4:"red"

  }
  this.state=[
    "Harvested",  
"Processed",  
"Packed",     // 2
"ForSale",    // 3
"Sold",       // 4
"Shipped",    // 5
"Received",   // 6
"Purchased"
  ]
    }

    render(){

        return(

            <div >

              { this.props.unions.map((union, key) => {
                return(
                  <div className="product" key={key} >
                    <div className="product_organic"style={{background:this.color[union.productstate]}} > {this.state[union.productstate]}</div>
                    <ul id="postList" className="list-group list-group-flush">
                      <li className="list-group-item">
                      <p>UPC :   <input  value={union.upc.toNumber()}/></p>
                      <p>Union Number :   <input  value={union.uNumber.toNumber()}/></p>  
                      <p>Crop NAME: <input value={union.cropName} /> </p>
                      <p>Price:  {union.price.toNumber()} </p>
                        {/* <p>ABOUT:   {product.productdescription}</p> */}

                      </li>
                    </ul>
                    </div>

                )
              }
              )
            }


            </div>


        )
    }
}

export default DisplayUnionOverview;
