import React from "react";
import { Component } from "react";

class Product extends  Component{

    render(){


        return(

            <div className="container-fluid mt-5">
            <div className="row">
              <main role="main" className="col-lg-12 ml-auto mr-auto" style={{ maxWidth: '500px' }}>
              <div className="content mr-auto ml-auto">
                  <p>&nbsp;</p>
                  <h2>REGISTER PRODUCT WITH CROP-ID </h2>
                    <form onSubmit={(event) => {
                      event.preventDefault()
                      const productname=this.productname.value
                      const price=this.price.value
                      const cropid=this.cropid.value
                      this.props.postProductCrop(event,productname,
                        price,cropid)
                      }}>

                    <div className="form-group mr-sm-2">
                      <input
                        id="productname"
                        type="text"
                        ref={(input) => { this.productname = input }}
                        className="form-control"
                        placeholder="enter the product name"
                        required />

                    </div>

                    <div className="form-group mr-sm-2">
                      <input
                        id="price"
                        type="number"
                        ref={(input) => { this.price = input }}
                        className="form-control"
                        placeholder="Enter the price"
                        required />

                    </div>

                    <div className="form-group mr-sm-2">
                      <input
                        id="cropid"
                        type="number"
                        ref={(input) => { this.cropid = input }}
                        className="form-control"
                        placeholder="Enter the cropid"
                        required />

                    </div>


                    <button type="submit" className="btn btn-primary btn-block">Submit products</button>
                 </form>


                </div>

                <div className="content mr-auto ml-auto">
                  <p>&nbsp;</p>
                  <h2>REGISTER PRODUCT WITH Union-ID </h2>
                    <form onSubmit={(event) => {
                      event.preventDefault()
                      const productname=this.productname.value
                      const price=this.price.value
                      const unionid=this.cropid.value
                      this.props.postProductUnion(event,productname,
                        price,unionid)
                      }}>

                    <div className="form-group mr-sm-2">
                      <input
                        id="productname"
                        type="text"
                        ref={(input) => { this.productname = input }}
                        className="form-control"
                        placeholder="enter the product name"
                        required />

                    </div>

                    <div className="form-group mr-sm-2">
                      <input
                        id="price"
                        type="number"
                        ref={(input) => { this.price = input }}
                        className="form-control"
                        placeholder="Enter the price"
                        required />

                    </div>

                    <div className="form-group mr-sm-2">
                      <input
                        id="unionid"
                        type="number"
                        ref={(input) => { this.unionid = input }}
                        className="form-control"
                        placeholder="Enter the union id"
                        required />

                    </div>


                    <button type="submit" className="btn btn-primary btn-block">Submit products</button>
                 </form>
                </div>


                <div className="content mr-auto ml-auto">
                  <p>&nbsp;</p>
                  <h2>REGISTER PRODUCT WITHOUT CROP-ID AND UNION-ID</h2>
                    <form onSubmit={(event) => {
                      event.preventDefault()
                      const productname=this.productname.value
                      const price=this.price.value
                      this.props.postProduct(event,productname,
                        price)
                      }}>

                    <div className="form-group mr-sm-2">
                      <input
                        id="productname"
                        type="text"
                        ref={(input) => { this.productname = input }}
                        className="form-control"
                        placeholder="enter the product name"
                        required />

                    </div>

                    <div className="form-group mr-sm-2">
                      <input
                        id="price"
                        type="number"
                        ref={(input) => { this.price = input }}
                        className="form-control"
                        placeholder="Enter the price"
                        required />

                    </div>





                    <button type="submit" className="btn btn-primary btn-block">Submit products</button>
                 </form>
            </div>
            </main>
        </div>
        </div>

        )
    }



}

export default Product;



